INSERT INTO book (bookID, title, author, description, genre, yearPublic, month, day, fullyear, publishDate, imageURL)  VALUES (     '968.The_Da_Vinci_Code',     'The Da Vinci Code',     'Dan Brown (Goodreads Author)',     'ISBN 9780307277671 moved to this edition. While in Paris, Harvard symbologist Robert Langdon is awakened by a phone call in the dead of the night. The elderly curator of the Louvre has been murdered inside the museum, his body covered in baffling symbols. As Langdon and gifted French cryptologist Sophie Neveu sort through the bizarre riddles, they are stunned to discover a trail of clues hidden in the works of Leonardo da Vinci—clues visible for all to see and yet ingeniously disguised by the painter. Even more startling, the late curator was involved in the Priory of Sion—a secret society whose members included Sir Isaac Newton, Victor Hugo, and Da Vinci—and he guarded a breathtaking historical secret. Unless Langdon and Neveu can decipher the labyrinthine puzzle—while avoiding the faceless adversary who shadows their every move—the explosive, ancient truth will be lost forever.',     '["Fiction", "Mystery", "Thriller", "Suspense", "Mystery Thriller", "Historical Fiction", "Adventure", "Novels", "Crime", "Adult"]',     2006, 3, 28, 2006, '2006-03-28 00:00:00',     'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1579621267l/968.jpg' );
INSERT INTO book (bookID, title, author, description, genre, yearPublic, month, day, fullyear, publishDate, imageURL) VALUES (     '929.Memoirs_of_a_Geisha',     'Memoirs of a Geisha',     'Arthur Golden',     'A literary sensation and runaway bestseller, this brilliant debut novel presents with seamless authenticity and exquisite lyricism the true confessions of one of Japan\'s most celebrated geisha. In Memoirs of a Geisha, we enter a world where appearances are paramount, where a girl\'s virginity is auctioned to the highest bidder, where women are trained to beguile the most powerful men, and where love is scorned as illusion. It is a unique and triumphant work of fiction - at once romantic, erotic, suspenseful - and completely unforgettable.',     '["Fiction", "Historical Fiction", "Romance", "Historical", "Classics", "Japan", "Adult", "Novels", "Asia", "Adult Fiction"]',     2005, 11, 22, 2005, '2005-11-22 00:00:00',     'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1409595968l/929.jpg' );
INSERT INTO book (bookID, title, author, description, genre, yearPublic, month, day, fullyear, publishDate, imageURL) VALUES (     '5297.The_Picture_of_Dorian_Gray',     'The Picture of Dorian Gray',     'Oscar Wilde, Jeffrey Eugenides (Introduction)',     'Written in his distinctively dazzling manner, Oscar Wilde’s story of a fashionable young man who sells his soul for eternal youth and beauty is the author’s most popular work. The tale of Dorian Gray’s moral disintegration caused a scandal when it first appeared in 1890, but though Wilde was attacked for the novel’s corrupting influence, he responded that there is, in fact, “a terrible moral in Dorian Gray.” Just a few years later, the book and the aesthetic/moral dilemma it presented became issues in the trials occasioned by Wilde’s homosexual liaisons, which resulted in his imprisonment. Of Dorian Gray’s relationship to autobiography, Wilde noted in a letter, “Basil Hallward is what I think I am: Lord Henry what the world thinks me: Dorian what I would like to be—in other ages, perhaps.',     '["Classics", "Fiction", "Horror", "Fantasy", "Literature", "Gothic", "Novels", "19th Century", "LGBT", "Classic Literature"]',     2004, 6, 1, 2004, '2004-06-01 00:00:00',     'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1546103428l/5297.jpg' );
INSERT INTO book (bookID, title, author, description, genre, yearPublic, month, day, fullyear, publishDate, imageURL)  VALUES (     '18405.Gone_with_the_Wind',     'Gone with the Wind',     'Margaret Mitchell',     'Scarlett O\'Hara, the beautiful, spoiled daughter of a well-to-do Georgia plantation owner, must use every means at her disposal to claw her way out of the poverty she finds herself in after Sherman\'s March to the Sea.',     '["Classics", "Historical Fiction", "Fiction", "Romance", "Historical", "War", "Literature", "Civil War", "Historical Romance", "Novels"]',     1999, 4, 1, 1999, '1999-04-01 00:00:00',     'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1551144577l/18405._SY475_.jpg' );
INSERT INTO book (bookID, title, author, description, genre, yearPublic, month, day, fullyear, publishDate, imageURL)  VALUES (     '11870085-the-fault-in-our-stars',     'The Fault in Our Stars',     'John Green (Goodreads Author)',     'Despite the tumor-shrinking medical miracle that has bought her a few years, Hazel has never been anything but terminal, her final chapter inscribed upon diagnosis. But when a gorgeous plot twist named Augustus Waters suddenly appears at Cancer Kid Support Group, Hazel\'s story is about to be completely rewritten. Insightful, bold, irreverent, and raw, The Fault in Our Stars is award-winning author John Green\'s most ambitious and heartbreaking work yet, brilliantly exploring the funny, thrilling, and tragic business of being alive and in love.',     '["Young Adult", "Romance", "Fiction", "Contemporary", "Realistic Fiction", "Teen", "Coming Of Age", "Drama", "Novels", "Love"]',     2012, 1, 10, 2012, '2012-01-10 00:00:00',     'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1360206420l/11870085.jpg' );
INSERT INTO book (bookID, title, author, description, genre, yearPublic, month, day, fullyear, publishDate, imageURL)  VALUES (     '386162.The_Hitchhiker_s_Guide_to_the_Galaxy',     'The Hitchhiker\'s Guide to the Galaxy',     'Douglas Adams',     'Seconds before the Earth is demolished to make way for a galactic freeway, Arthur Dent is plucked off the planet by his friend Ford Prefect, a researcher for the revised edition of The Hitchhiker\'s Guide to the Galaxy who, for the last fifteen years, has been posing as an out-of-work actor. Together this dynamic pair begin a journey through space aided by quotes from The Hitchhiker\'s Guide (A towel is about the most massively useful thing an interstellar hitchhiker can have) and a galaxy-full of fellow travelers: Zaphod Beeblebrox—the two-headed, three-armed ex-hippie and totally out-to-lunch president of the galaxy, Trillian, Zaphod\'s girlfriend (formally Tricia McMillan), whom Arthur tried to pick up at a cocktail party once upon a time zone, Marvin, a paranoid, brilliant, and chronically depressed robot, Veet Voojagig, a former graduate student who is obsessed with the disappearance of all the ballpoint pens he bought over the years.',     '["Science Fiction", "Fiction", "Humor", "Fantasy", "Classics", "Comedy", "Science Fiction Fantasy", "Audiobook", "Adventure", "Novels"]',     2007, 6, 23, 2007, '2007-06-23 00:00:00',     'https://i.gr-assets.com/images/S/compressed.photo.goodreads.com/books/1559986152l/386162.jpg' );
